function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }



    if (newValue == "10") {
        g_form.addOption("type_of_award_appreciation", "10", "Pat on the Back Award");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "10", "Rs.300");
    }

    if (newValue == "20") {
        g_form.addOption("type_of_award_appreciation", "20", "On-Spot Award");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "20", "Rs.400");
    }

    if (newValue == "30") {
        g_form.addOption("type_of_award_appreciation", "30", "Best Employee of the month");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "30", "Rs.500");
    }

    if (newValue == "40") {
        g_form.addOption("type_of_award_appreciation", "40", "Successful Go-Live Appreciation");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "40", "Rs.250");
    }


    if (newValue == "50") {
        g_form.addOption("type_of_award_appreciation", "50", "Escalation Handling Appreciation");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "50", "Rs.200");
    }


    if (newValue == "60") {
        g_form.addOption("type_of_award_appreciation", "60", "Best Newcomer Award");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "60", "Rs.600");
    }


    if (newValue == "70") {
        g_form.addOption("type_of_award_appreciation", "70", "Rising Star Award");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "70", "Rs.800");
    }

    if (newValue == "80") {
        g_form.addOption("type_of_award_appreciation", "80", "Extraordinary Employee Award");

        g_form.clearOptions("e_wallet_amount_to_be_added");
        g_form.addOption("e_wallet_amount_to_be_added", "80", "Rs .1000 ");
        }
