function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {

        return;
    }

    if (newValue == "10") {
        g_form.clearOptions("e_voucher_brand");
        g_form.addOption("e_voucher_brand", "10", "Flipkart");
