		g_form.clearOptions("e_voucher_amount");
		g_form.addOption("e_voucher_amount", "40", "Rs.400");
		g_form.addOption("e_voucher_amount", "50", "Rs.500");
		g_form.addOption("e_voucher_amount", "60", "Rs.750");
		g_form.addOption("e_voucher_amount", "70", "Rs.1000");
    }
    if (newValue == "40") {
        g_form.clearOptions("e_voucher_brand");
        g_form.addOption("e_voucher_brand", "60", "Zomato");
        g_form.addOption("e_voucher_brand", "70", "Swiggy");

		g_form.clearOptions("e_voucher_amount");
		g_form.addOption("e_voucher_amount", "10", "Rs.100");
		g_form.addOption("e_voucher_amount", "20", "Rs.200");
		g_form.addOption("e_voucher_amount", "40", "Rs.400");
		g_form.addOption("e_voucher_amount", "70", "Rs.1000");
    }
    if (newValue == "30") {
        g_form.clearOptions("e_voucher_brand");
        g_form.addOption("e_voucher_brand", "80", "Puma");
        g_form.addOption("e_voucher_brand", "90", "Adidas");
